import React, { useState } from 'react';
import axios from 'axios';

function HospitalDetails() {
    const [hospitalId, setHospitalId] = useState('');
    const [hospital, setHospital] = useState(null);

    const handleIdChange = (event) => {
        setHospitalId(event.target.value);
    };

    const handleFetchData = async () => {
        try {
            const response = await axios.get(`http://localhost:3001/new/${hospitalId}`);
            setHospital(response.data);
        } catch (error) {
            console.error('Error fetching hospital data:', error);
        }
    };

    return (
        <div>
            <h1>Hospital Details</h1>
            <div>
                <label htmlFor="hospitalId">Hospital ID:</label>
                <input type="text" id="hospitalId" value={hospitalId} onChange={handleIdChange} />
                <button onClick={handleFetchData}>Fetch Data</button>
            </div>
            {hospital && (
                <div>
                    <p>Name: {hospital.HospitalName}</p>
                    <p>Phone: {hospital.HospitalPhoneNo}</p>
                    <p>specialist: {hospital.specialist}</p>
                    <p>HospitalNo: {hospital.HospitalNo}</p>
                </div>
            )}
        </div>
    );
}

export default HospitalDetails;
