import React, { useState } from 'react';
import axios from 'axios';

function HospitalForm() {
    const [hospitalName, setHospitalName] = useState('');
    const [hospitalPhone, setHospitalPhone] = useState('');
    const [hospitalSubject, setHospitalSubject] = useState('');
    const [hospitalAttendance, setHospitalAttendance] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (!/^\d+$/.test(hospitalPhone)) {
            alert('Please enter a valid phone number');
            return;
        }
        try {
            const response = await axios.post('http://localhost:3001/new', {
                HospitalName: hospitalName,
                HospitalPhoneNo: hospitalPhone,
                specialist: hospitalSubject,
                HospitalNo: hospitalAttendance,
            });
            console.log('Hospital data saved:', response.data);
        } catch (error) {
            console.error('Error saving hospital data:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Hospital Name:
                <input type="text" value={hospitalName} onChange={(e) => setHospitalName(e.target.value)} />
            </label>
            <br />
            <label>
                Hospital Phone:
                <input type="tel" value={hospitalPhone} onChange={(e) => setHospitalPhone(e.target.value)} />
            </label>
            <br />
            <label>
                Hospital Subject:
                <input type="text" value={hospitalSubject} onChange={(e) => setHospitalSubject(e.target.value)} />
            </label>
            <br />
            <label>
                Hospital Attendance:
                <input type="text" value={hospitalAttendance} onChange={(e) => setHospitalAttendance(e.target.value)} />
            </label>
            <br />
            <button type="submit">Submit</button>
        </form>
    );
}

export default HospitalForm;
