import React, { useState } from 'react';

function HospitalEditor() {
    const [id, setId] = useState('');
    const [hospitalName, setHospitalName] = useState('');
    const [hospitalPhoneNo, setHospitalPhoneNo] = useState('');
    const [specialist, setSpecialist] = useState('');
    const [hospitalNo, setHospitalNo] = useState('');

    const handleIdChange = (event) => setId(event.target.value);
    const handleHospitalNameChange = (event) =>
        setHospitalName(event.target.value);
    const handleHospitalPhoneNoChange = (event) =>
        setHospitalPhoneNo(event.target.value);
    const handleSpecialistChange = (event) => setSpecialist(event.target.value);
    const handleHospitalNoChange = (event) => setHospitalNo(event.target.value);

    const handleEdit = () => {

        fetch(`http://localhost:3001/students/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                hospitalName,
                hospitalPhoneNo,
                specialist,
                hospitalNo,
            }),
        })
            .then((response) => {
                if (response.ok) {
                    // Success! Notify user or do any other necessary actions
                } else {
                    // Oops! Something went wrong. Handle the error
                }
            })
            .catch((error) => {
                // Handle network error
            });
    };

    return (
        <div>
            <label>
                Enter Hospital ID:
                <input type="text" value={id} onChange={handleIdChange} />
            </label>
            <br />
            <label>
                Hospital Name:
                <input
                    type="text"
                    value={hospitalName}
                    onChange={handleHospitalNameChange}
                />
            </label>
            <br />
            <label>
                Hospital Phone No:
                <input
                    type="text"
                    value={hospitalPhoneNo}
                    onChange={handleHospitalPhoneNoChange}
                />
            </label>
            <br />
            <label>
                Specialist:
                <input
                    type="text"
                    value={specialist}
                    onChange={handleSpecialistChange}
                />
            </label>
            <br />
            <label>
                Hospital No:
                <input
                    type="text"
                    value={hospitalNo}
                    onChange={handleHospitalNoChange}
                />
            </label>
            <br />
            <button onClick={handleEdit}>Edit Hospital</button>
        </div>
    );
}

export default HospitalEditor;
